define([], function () {
    return {
        "tasks": {
            collection: "tasks", fields: [
                {field: "task", type: "string", mandatory: true},
                {field: "description", type: "string"},
                {field: "assignToId", type: "fk", mandatory: true, collection: "users", set: ["username"]},
                {field: "watches", type: "fk", collection: "users", set: ["username"], multiple: true},
                {field: "parentId", type: "fk", collection: "projects", set: ["name"]}
            ]
        },
        "users": {
            collection: "users", fields: [
                {field: 'username', type: "string", mandatory: true}
            ]
        },
        "projects": {
            collection: "projects", fields: [
                {field: "name", type: "string"}
            ]
        }

    }
})
