define([], function () {
    return {
        "tasks": {
            collection: "tasks", fields: [
                {field: "task", type: "string", mandatory: true},
                {field: "duedate", type: "date"},
                {field: "owner", type: "fk", mandatory: true, collection: "users", set: ["name"]},
                {field: "watch", type: "fk", collection: "users", set: ["name"], multiple: true},
                {field: "project", type: "fk", collection: "projects", set: ["name"]},
                {field: "department", type: "fk", collection: "departments"},
                {field: "state", type: "fk", collection: "status", set: ['name']},
                {field: "type", type: "fk", collection: "types", set: ['name']}
            ]
        },
        "users": {
            collection: "users", fields: [
                {field: 'name', type: "string", mandatory: true},
                {field: 'reporting_to', type: "fk",collection:"users", set:['name']}
            ]
        },
        "projects": {
            collection: "projects", fields: [
                {field: "name", type: "string"},
                {field: "parent", type: "fk", collection:"projects", set: ['projects']},
                {field: "manager", type: "fk", collection:"users", set: ['name']}
            ]
        },
        "status":{
            collection: "status", fields:[
                {field: "name", type:"string",options:["New","In Process","Completed"]}
            ]
        },
        "types":{
            collection: "types", fields:[
                {field: "name", type:"string",options:["Task","Bug"]}
            ]
        },
        "departments":{
            collection: "departments", fields:[
                {field: "name", type:"string", mandatory:true}
            ]
        }


    }
})
